import React, { useContext , useState} from 'react'
import noteContext from '../context/notes/noteContext'
function AddNote() {
    const context = useContext(noteContext);
    const {addNote} = context;

    const [note , setNote] = useState({title:"" , description:"" , tag:"" }) 
    const handleClick =(e)=>{
        e.preventDefault();
       addNote(note.title , note.description , note.tag);
       setNote({title:"" , description:"" , tag:"" })
    }

    const onChange=(e)=>{
       setNote({...note , [e.target.name]: e.target.value})
    }
    return (
        <div>
            <div className="container my-3">
                <h1>Add Notes </h1>
                <form>
                    <div className="mb-3">
                        <label htmlFor="title" className="form-label">Title</label>
                        <input type="text" className="form-control" id="title" name="title" aria-describedby="emailHelp" onChange={onChange} value={note.title} minLength={3} required/>
                    </div>
                    <div className="mb-3">
                        <label htmlFor="description" className="form-label" >description</label>
                        <input type="text" className="form-control" id="description" name="description" onChange={onChange} value={note.description} minLength={3} required/>
                    </div>
                    <div className="mb-3">
                        <label htmlFor="tag" className="form-label" >tag</label>
                        <input type="text" className="form-control" id="tag" name="tag" onChange={onChange} value={note.tag} minLength={3} required/>
                    </div>

                    <button disabled={note.title.length<3 || note.description.length<3 } type="submit"  className="btn btn-primary" onClick={handleClick}>Add Note</button>
                </form>
            </div>
        </div>
    )
}

export default AddNote
