import { useState } from "react";
import NoteContext from "./noteContext";

const NoteState = (props) => {
    const host = "http://localhost:5000";
    const notesInitial = [];

    const [notes, setNotes] = useState(notesInitial);

    // Get all notes  
    const getnotes = async () => {

        const response = await fetch(`${host}/api/notes/fetchallnotes`, {
            method: "GET",
            headers: {
                "Content-Type": "application/json",
                "auth-token": "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VyIjp7ImlkIjoiNjIyYzQwNjYyZTZmNjFmZmVmY2E1NzRkIn0sImlhdCI6MTY0NzA2NzI2M30.uezmBiRR8TDBBQ0OHXsHucznQXB6KQe5sQhrnzMfPRI",
            },
        });
        const json = await response.json();
        console.log(json)

    };
    // Add note
    const addNote = async (title, description, tag) => {

        const response = await fetch(`${host}/api/notes/addnote`, {
            method: "POST",
            headers: {
                "Content-Type": "application/json",
                "auth-token": "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VyIjp7ImlkIjoiNjIyYzQwNjYyZTZmNjFmZmVmY2E1NzRkIn0sImlhdCI6MTY0NzA2NzI2M30.uezmBiRR8TDBBQ0OHXsHucznQXB6KQe5sQhrnzMfPRI",
            },
            body: JSON.stringify({ title, description, tag })
        });
        // const json = response.json();


        const note = {
            _id: "622b0551890f1d12473288ad7",
            user: "6228bd7cab58086b170f2cb2",
            title: title,
            description: description,
            tag: tag,
            date: "2022-03-11T08:16:17.292Z",
            __v: 0,
        };
        setNotes(notes.concat(note));
    };

    // delete note
    const deleteNote = (id) => {
        // console.log("deleting the note with "+ id);
        const newNotes = notes.filter((note) => {
            return note._id !== id;
        });
        setNotes(newNotes);
    };

    // Edit note
    const editNote = async (id, title, descripttion, tag) => {

        const response = await fetch(`${host}/api/notes/updateNote/${id}`, {
            method: "POST",
            headers: {
                "Content-Type": "application/json",
                "auth-token": "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VyIjp7ImlkIjoiNjIyYzQwNjYyZTZmNjFmZmVmY2E1NzRkIn0sImlhdCI6MTY0NzA2NzI2M30.uezmBiRR8TDBBQ0OHXsHucznQXB6KQe5sQhrnzMfPRI",
            },
            body: JSON.stringify({ title, descripttion, tag })
        });
        return response.json()

    // logic to edit note in client
    for (let index = 0; index < notes.length; index++) {
        const element = notes[index];

        if (element._id === id) {
            element.title === title,
                element.descripttion === descripttion,
                element.tag === tag
        }
    }
};

return (
    <NoteContext.Provider value={{ notes, addNote, deleteNote, editNote, getnotes }}>
        {props.children}
    </NoteContext.Provider>
);
};
export default NoteState;
